function generateuscisbarcode(){var e=document.getElementById("barcodeValue").value,t=document.getElementById("barcodeType").value,o=document.getElementById("showText").value;JsBarcode("#uscisbarcode",e,{format:t,displayValue:o,height:80}),document.getElementById("savetxt").style.display="block",document.getElementById("dwnuscisbarcode").style.display="block"}function generateusciscustombarcode(){var e=document.getElementById("barcodeValue").value,t=document.getElementById("barcodeType").value,o=document.getElementById("showText").value,d=document.getElementById("barcodeWidth").value,c=document.getElementById("barcodeHeight").value,a=document.getElementById("barcodeFontSize").value,n=document.getElementById("barcodeLineColor").value;JsBarcode("#customuscisbarcode",e,{format:t,displayValue:o,width:d,height:c,fontSize:a,lineColor:n}),document.getElementById("savetxt").style.display="block",document.getElementById("dwnuscisbarcode").style.display="block"}function saveuscisbarcode(){downloadImage(document.querySelector("img#uscisbarcode").src,"mybarcode.png")}function saveusciscustombarcode(){downloadImage(document.querySelector("img#customuscisbarcode").src,"mycustombarcode.png")}async function downloadImage(e,t){const o=await fetch(e),d=await o.blob(),c=URL.createObjectURL(d),a=document.createElement("a");a.href=c,a.download=t,document.body.appendChild(a),a.click(),document.body.removeChild(a)}